package PackOne;

public class IfElseIfDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=45;
		if(x==90){
			System.out.println("the two values are equal");
		}
		else if(x>90){
			System.out.println("the value is greater than 90");
		}
		else {
			System.out.println("the value is less than 90");
		}

	}

}
